import { React ,useEffect,useState} from 'react';
import axios from 'axios';
const Home = () => {
    const [Posts , setPosts] = useState([]);
    const apiEnd = 'https://jsonplaceholder.typicode.com/posts';

    useEffect(()=>{
        const getPosts = async () =>{
            const { data : res} =  await axios.get(apiEnd)
            setPosts(res)
        };
        getPosts();
    },[])
    const addPost = () =>{
        const post = {title:"New Post" , body:"new"};
        axios.post(apiEnd , post);
        setPosts([post, ...Posts])
    }
    const handelupdate = async (po)=>{
        post.title = "update title";
        await axios.put (apiEnd + "/" + po.id)
        const postsClone = [...Posts]
        const index = postsClone.indexOf(po)
        postsClone[index] = {...post};
        setPosts(postsClone)

    }
    const handeldelete = (p) =>{
        axios.delete(apiEnd + "/" + p.id + p)
        setPosts(Posts.filter(p => p.id !== p.id))
    }
    return (
<>
    
            <div className='container'>
                <h2>there are {Posts.length}post in the database</h2>
                <button onClick={addPost} className='btn btn-primary '  >Add Post</button>    
                <table className='table'>
                  <thead>
                     <tr>
                       <th>Title</th>
                       <th>Update</th>
                       <th>Delete</th>
                     </tr>
                     <tbody>
                      {Posts.map((P) => (
                          <tr key={P.id}>
                              <td> {P.title} </td>
                                <td> <button onClick={()=>handelupdate(P)} className='btn  btn-info me-5 btn-sm'>Update</button></td> 
                                <td> <button onClick={()=> handeldelete(P)}  className='btn  btn-danger btn-sm '>Delete</button></td>    
                          </tr>
                      ))}
                     </tbody>
                  </thead>
              </table>
            </div>
</>
    );
}

export default Home;
